export default {
  name: "Home",
  data: function data() {
    return {
      nowTime: '',
      userIn: localStorage.getItem("userIn") ? JSON.parse(localStorage.getItem("userIn")) : {}
    };
  },
  mounted: function mounted() {
    var _this = this;

    var yy = new Date().getFullYear();
    var mm = new Date().getMonth() + 1;
    var dd = new Date().getDate();
    var hh = new Date().getHours();
    var mf = new Date().getMinutes() < 10 ? '0' + new Date().getMinutes() : new Date().getMinutes();
    var ss = new Date().getSeconds() < 10 ? '0' + new Date().getSeconds() : new Date().getSeconds();
    _this.nowTime = yy + '-' + mm + '-' + dd + ' ' + hh + ':' + mf + ':' + ss;
    console.log(this.nowTime);
  }
};
